import React, { useState,useEffect } from 'react'
import Mtitle from './MainTitle';
import Navbar from './NavBar';
import Gallery from './Gallery';
import Clock from './components/Clock.js';
import './Style.css';

function App() {
  let [pageNumber , setPageNumber] = useState(1);
  let [fetchedData , updateFetchedData] =useState([]);
  let { info , results } = fetchedData;
  let api =  `https://rickandmortyapi.com/api/character/?page=${pageNumber}`;

  useEffect(()=>{
      (async function(){
          let data = await fetch(api).then((res) => res.json());
          updateFetchedData(data);
        })()},[api])


      const handleClick = (id) => {
        if(id != null){
          show_clicked_data(id);
        }
      };
    
      useEffect(() => {
        const handleDocumentClick = (event) => {
            const targetElement = event.target;
            handleClick(targetElement.getAttribute('alt'));
        };
        document.addEventListener('click', handleDocumentClick);
        return () => {
      document.removeEventListener('click', handleDocumentClick);
    };
  }, []);

  
  const show_clicked_data = (clicked_id) => {
    results.map((x) => {
      let {id , name , status ,  species , gender , origin , location , image ,episode , url , created } = x;
      if(clicked_id == id){
        console.log(id,name,status);
      }
    });
  };

  return (
    <div className="app-container">
      <header>
        
        <Clock/>{}
        <Mtitle/>{}
        <Navbar/>{}
        <Gallery results={results} setPageNumber={setPageNumber} pageNumber={pageNumber}/>
      </header>
     
    </div>
  );
}

export default App;
