import React from 'react';

const Pagination = ({setPageNumber,pageNumber}) => { 

    let x = 0;
    let next = () => {
        if(pageNumber<42){
            setPageNumber((x) => x+1);
        }else{
            setPageNumber(42);
        }
    };
    let prev = () => {
        if(pageNumber>1){
            setPageNumber((x) => x-1);
        }else{
            setPageNumber((1)); 
        }
    };


    function end_start_of_pages(pageNumber){
        let style_props_button;
        switch (pageNumber){
            case 42:
                status_display = "badge bg-success fw-normal fs-5";
                break;
            case 1:
                status_display = "badge bg-danger fw-normal fs-5";
                break;
            default:
                status_display = "badge bg-danger fw-normal fs-5";
                break;
        }
        return 
    }

    return(
        <div className='container d-flex justify-content-center gap-5'>
            <button className='btn btn-secondary my-5 fs-5 fw-bold' onClick={prev}> &#60;&#60; prev </button>
            <button className='btn btn-secondary my-5 fs-5 fw-bold' onClick={next}> next &#62;&#62; </button>
        </div>
    );
};

export default Pagination;
