import React, { useState,useEffect } from 'react'
import Mtitle from './MainTitle';
import Navbar from './NavBar';
import Gallery from './Gallery';
import Clock from './components/Clock.js';
import './Style.css';

function App() {
  let [pageNumber , setPageNumber] = useState(1);
  let [fetchedData , updateFetchedData] =useState([]);
  let { info , results } = fetchedData;
  let api =  `https://rickandmortyapi.com/api/character/?page=${pageNumber}`;

  useEffect(()=>{
      (async function(){
          let data = await fetch(api).then((res) => res.json());
          updateFetchedData(data);
        })()},[api])

    

        useEffect(() => {
          // Attach a click event listener to the document (you can use any parent element)
          const handleDocumentClick = (event) => {
            const targetElement = event.target;
              let clicked_id = targetElement.getAttribute('alt');
          };
      
          document.addEventListener('click', handleDocumentClick);
      
          // Cleanup the event listener when the component unmounts
          return () => {
            document.removeEventListener('click', handleDocumentClick);
          };
        }, []); 


      const handleDocumentClick = (event) => {
        const targetElement = event.target;
        let clicked_id = targetElement.getAttribute('alt');
        if(clicked_id != null){
          let single_api = `https://rickandmortyapi.com/api/character/=${clicked_id}`;
          let [single_fetched_Data , update_single_Fetched_Data] =useState([]);
          let { single_result } = single_fetched_Data;
          useEffect(()=>{
            (async function(){
              let single_data = await fetch(single_api).then((res) => res.json());
              updateFetchedData(single_data);
          })()},[single_api]);
          console.log(single_result);
        }
      };
      document.addEventListener('click', handleDocumentClick);
      return () => {
    document.removeEventListener('click', handleDocumentClick);
  };


  return (
    <div className="app-container">
      <header>
        
        <Clock/>{}
        <Mtitle/>{}
        <Navbar/>{}
        <Gallery results={results} setPageNumber={setPageNumber} pageNumber={pageNumber}/>
      </header>
     
    </div>
  );
}

export default App;
