import React from 'react';

const Pagination = ({setPageNumber}) => { 

    let next = () => {
        setPageNumber((x) => x+1);
    };
    let prev = () => {
        setPageNumber((x) => x-1);
    };

    return(
        <div className='container d-flex justify-content-center gap-5'>
            <button className='btn btn-secondary my-5 fs-5 fw-bold' onClick={prev}> &#60;&#60; prev </button>
            <button className='btn btn-light my-5 fs-5 gap-3' > 2 </button>
            <button className='btn btn-light my-5 fs-5' > 3 </button>
            <button className='btn btn-light my-5 fs-5' > 4 </button>
            <button className='btn btn-secondary my-5 fs-5 fw-bold' onClick={next}> next &#62;&#62; </button>
        </div>
    );
};

export default Pagination;
