import React from 'react'
import '../GalleryStyle.css'
import C_Modal from './C_Modal';
import { Modal } from 'bootstrap';


function Badge_color_status(status){
    let status_display;
    switch (status){
        case "Alive":
            status_display = "badge bg-success fw-normal";
            break;
        case "Dead":
            status_display = "badge bg-danger fw-normal";
            break;
        case "unknown":
            status_display = "badge bg-secondary fw-normal";
            break;
    }
    return status_display;
}

function Length_of_String(name){
    let name_suitble_size;
    if (name.length < 20){
        name_suitble_size = "fs-5 fw-bold mb-2";
    }
    else{
        name_suitble_size = "fs-6 fw-bold mb-2";
    }
    return name_suitble_size;
}

function Pop_Dialog(x){
    <C_Modal x={x}/>
}

const Cards = ({results}) => {
    let display;
    if(results){
        display = results.map(x=>{
            let {id , name , status ,  species , gender , origin , location , image ,episode , url , created } = x;
            return (
            <div key={id} className='col-4 text-light position-realtive' id='Character_Card'>
                <div className=''>
                    <img src={image} alt="" className="img-fluid" id="CardImage" data-toggle="modal" data-target="modal"  onClick={Pop_Dialog(x)}/>
                    <div id="Cardblur"></div>
                    <div className='content'>
                        <div className={Length_of_String(name)}>{name}<span></span> <span className={Badge_color_status(status)} id='status_badge'>{status}</span></div>
                        <div className="mb-4">
                            <div className="fs-6 bg-dark fw-light text-light">{gender} - {species}</div>
                            <div className="fs-6 fw-light" >last seen in-<br></br><span className='fs-6 text-success fw-normal'>{location.name}</span></div>
                        </div>
                    </div>
                </div>
            </div>
            );
        });
    }else{
        display = "404 - Not Found :("
    }
    return <> {display} </>;
}


export default Cards
